    function handleChange(event) {
        const newValue = event.target.value;
        setInput(newValue);
    }

    function addItem(){
        setItems(prevItems => {
            return [...prevItems, inputTex]
        })
        setInput("");
    }

    return (
    <div className="form">
        <input onChange={handleChange} type="text" value={inputText}></input>
        <button onClick={addItem}>
            <span>Add</span>
        </button>
    </div>
    );
}

export default InputArea;
