import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import notes from "../notes";
import GitRDuns from "./GitRDuns"

// function newNote(notes) {
// return (
//     <Note 
//         key={notes.id}
//         title={notes.title}
//         content={notes.content}
//     />
//     )

function App(){

    const [inputTex, setInput] = useState('');
    const [items, setItems] = useState ([]);

    function handleChange(event) {
        const newValue = event.target.value;
        setInput(newValue);
    }

    function addItem(){
        setItems(prevItems => {
            return [...prevItems, inputTex]
        })
        setInput("");
    }

    function deleteItem(){
        console.log("delted")
    }

return ( 
<div>
    <Header/>
    <div className='container'>
        <div className='header'>
            <h1>Git-R-Done</h1>
            <div className="form">
                <input onChange={handleChange} type="text" value={inputTex}></input>
                <button onClick={addItem}>
                    <span>Add</span>
                </button>
            <div>
                <ul>
                    {items.map(gitrduns => (
                    <GitRDuns text={gitrduns} onChecked={deleteItem}/>
                    ))}
                </ul>
            </div>
            </div>
        </div>
    </div>
    <Footer/>
</div>
);
}

export default App;
